DEFAULT_CONFIG = "zapzap.config"
COUNTRIES_CSV = "countries.csv"

def getCredentials(config = DEFAULT_CONFIG):
    if os.path.isfile(config):
        f = open(config)
        
        phone = ""
        idx = ""
        pw = ""
        cc = ""
        
        try:
            for l in f:
                line = l.strip()
                if len(line) and line[0] not in ('#',';'):
                    
                    prep = line.split('#', 1)[0].split(';', 1)[0].split('=', 1)
                    
                    varname = prep[0].strip()
                    val = prep[1].strip()
                    
                    if varname == "phone":
                        phone = val
                    elif varname == "id":
                        idx = val
                    elif varname =="password":
                        pw =val
                    elif varname == "cc":
                        cc = val

            return (cc, phone, idx, pw);
        except:
            pass

    return 0

def dissectPhoneNumber(phoneNumber):
    try:
        with open(COUNTRIES_CSV, 'r') as csvfile:
            reader = csv.reader(csvfile, delimiter=',')
            for row in reader:
                if len(row) == 3:
                    country, cc, mcc = row
                else:
                    country,cc = row
                    mcc = "000"
                try:
                    if phoneNumber.index(cc) == 0:
                        print("Detected cc: %s"%cc)
                        return (cc, phoneNumber[len(cc):])

                except ValueError:
                    continue
                
    except:
        pass
    return False


